 
var ViewScript = {}
ViewScript.PartialLoad = function (parent, host, path, args, after)
{
    javascript: PartialScript.ShowLoading();
    var content = "";
    javascript: content = PartialScript.PartialLoad(host, path, args);
    $(parent).html(content);
    if (after && typeof (after) == "function") after(parent);
    javascript: PartialScript.CloseLoading();
}

ViewScript.AppendPartial = function (parent, host, path, args, after) {
    javascript: PartialScript.ShowLoading();
    var content = "";
    javascript: content = PartialScript.PartialLoad(host, path, args);
    $(parent).append(content);
    if (after && typeof (after) == "function") after(parent);
    javascript: PartialScript.CloseLoading();
}

ViewScript.Alert = function (message)
{
    javascript: AndroidScript.ShowToast(message);
}

